/**
 * Rule: avoid-new
 * Avoid creating new promises outside of utility libraries.
 */

'use strict';

var getDocsUrl = require('./lib/get-docs-url');

module.exports = {
  meta: {
    docs: {
      url: getDocsUrl('avoid-new')
    }
  },
  create: function create(context) {
    return {
      NewExpression: function NewExpression(node) {
        if (node.callee.name === 'Promise') {
          context.report({ node: node, message: 'Avoid creating new promises.' });
        }
      }
    };
  }
};